import React, { useEffect, useState } from "react";
import Hands from "./../../asstes/portfolio/hands.png";
import "./contact.css";

const Contact = () => {
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [subject, setSubject] = useState("");
  const [message, setMessage] = useState("");
  const [error1, setError1] = useState("");
  const [error2, setError2] = useState("");
  const [error3, setError3] = useState("");
  const [error4, setError4] = useState("");
  const [error, setError] = useState("");
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    subject: "",
    message: "",
  });
  // const getusers = () => {
  //   fetch("http://localhost:8002/api/user")
  //     .then((res) => res.json())
  //     .then((json) => console.log(json));
  // };

  // useEffect(() => {
  //   getusers();
  // }, []);
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post(
        "http://localhost:8002/submit-form",
        formData
      );
      console.log(response.data.message);
      // Optionally, reset form fields after successful submission
      setFormData({
        name: "",
        email: "",
        phone: "",
        subject: "",
        message: "",
      });
    } catch (error) {
      console.error("Failed to submit form:", error);
    }
  };
  // name validation
  const handleChange = (e) => {
    const inputName = e.target.value;
    setName(inputName);
    const hasRepeatedLetters = /(.)(\1)/g.test(inputName);
    const hasSymbols = /[!@#$%^&*(),.?":{}|<>]/g.test(inputName);
    const hasNumbers = /\d/g.test(inputName);

    if (inputName.length > 6) {
      setError("Name should be at least 6 letters");
    } else if (hasRepeatedLetters) {
      setError("Name should not have repeated letters");
    } else if (hasSymbols) {
      setError("Name should not contain symbols");
    } else if (hasNumbers) {
      setError("Name should not contain numbers");
    } else {
      setError("");
    }
  };

  //Phone Number validation

  const handleChange1 = (e) => {
    const inputPhone = e.target.value;
    setPhone(inputPhone);
    if (inputPhone.length > 12) {
      setError1("Phone number should be at least 10 numbers");
    } else {
      setError1("");
    }
  };

  //   EMAIL VALIDATION

  const handleChange2 = (e) => {
    const inputEmail = e.target.value;
    setEmail(inputEmail);
    // Email length validation
    if (inputEmail.length > 50) {
      setError2("Email should not exceed 50 characters");
    } else {
      // Email format validation
      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      if (!emailRegex.test(inputEmail)) {
        setError2("Invalid email format");
      } else {
        setError2("");
      }
    }
  };
  const handleChange3 = (e) => {
    const inputSubject = e.target.value;
    setSubject(inputSubject);
    // Email length validation
    if (inputSubject.length > 50) {
      setError3("Subject should not exceed 50 characters");
    } else {
      setError3("");
    }
  };
  const handleChange4 = (e) => {
    const inputMessage = e.target.value;
    setMessage(inputMessage);

    if (inputMessage.length > 200) {
      setError4("message should not exceed 200 characters");
    } else {
      setError4("");
    }
  };

  return (
    <div className="portfolio">
      <div className="container mx-auto max-w-7xl px-2 sm:px-6 lg:px-8">
        <div className="-mx-4 flex  flex-wrap">
          <div className="w-full px-4">
            <div className=" mb-12 text-center mx-auto max-w-[510px] item-center lg:mb-20 ">
              <h2 className="text-5xl portfolio-text">Contact With Me</h2>
            </div>
          </div>
        </div>
      </div>
      <div className="flex flex-wrap  justify-center items-center">
        <div className=" portfolio-card w-[432px] h-[680px] rounded-3xl mr-[38px] p-6 mb-10">
          <div className="rectangle-3-2 w-[383px] h-[228px] rounded-3xl bg-gray-300 mt-4 ">
            <img
              src={Hands}
              alt=""
              className="rounded-3xl transition-all duration-500 hover:scale-105 hover:-translate-y-1"
            />
          </div>
          <div className="flex  text-2xl mt-10 font-bold text-[233,237,245] mb-4">
            Dasari Vinay Reddy
          </div>
          <p className="text-gray-500 mb-10">Software Engineer</p>
          <p className="text-gray-700 mb-8">
            I am available for freelance work. Connect with me via and call in
            to my account.
          </p>
          <p className="text-container mb-3">
            Phone : +91 7799262831
            <span className="underline"></span>
          </p>
          <p className="text-container">
            Email : rvinay968@gmail.com
            <span className="underline"></span>
          </p>
        </div>
        <div className="portfolio-card w-[762px] h-[680px] rounded-3xl">
          <div className="isolate  px-6  ">
            <form onSubmit={handleSubmit} className=" mx-auto py-10 max-w-xl ">
              <div className="grid grid-cols-1 gap-x-8 gap-y-6 sm:grid-cols-2">
                <div>
                  <label
                    htmlFor="first-name"
                    className="block text-sm font-semibold leading-6 text-gray-900"
                  >
                    Your Name
                  </label>
                  <div className="mt-2.5">
                    <input
                      type="text"
                      name="first-name"
                      id="first-name"
                      autoComplete="given-name"
                      placeholder="Enter Name"
                      value={name}
                      onChange={handleChange}
                      className={`block w-full rounded-md border-0 px-3.5 py-2 text-gray-900 shadow-sm ring-1 ring-inset ${
                        error ? "ring-red-600" : "ring-gray-300"
                      } placeholder:text-gray-400 focus:ring-2 focus:ring-inset ${
                        error ? "focus:ring-red-600" : "focus:ring-indigo-600"
                      } sm:text-sm sm:leading-6`}
                    />
                    {error && (
