import React, { useState } from "react";
import Hands from "./../../asstes/portfolio/hands.png";
import "./contact.css";

const Contact = () => {
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [subject, setSubject] = useState("");
  const [message, setMessage] = useState("");
  const [error1, setError1] = useState("");
  const [error2, setError2] = useState("");
  const [error3, setError3] = useState("");
  const [error4, setError4] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate inputs before submitting
    if (!validateInputs()) {
      return;
    }

    try {
      // Send form data to backend
      const response = await axios.post("/submit-form", {
        name,
        phone,
        email,
        subject,
        message,
      });

      console.log("Form submitted successfully:", response.data);
      // Optionally reset form fields
      setName("");
      setPhone("");
      setEmail("");
      setSubject("");
      setMessage("");
    } catch (error) {
      console.error("Failed to submit form:", error);
      // Handle error state if needed
    }
    // e.preventDefault();
    // const { name, email, phonenumber, subject, message } = formData;
    // try {
    //   const response = await fetch("https://example.com:3001/api/sendmail", {
    //     method: "POST",
    //     headers: { "Content-Type": "application/json" },
    //     body: JSON.stringify({ name, email, phonenumber, subject, message }),
    //   });
    //   if (response.status === 200) {
    //     console.log("Email sent!. \nResponse:", response);
    //     setFormData((prevState) => ({ ...prevState, formSubmitted: true }));
    //   } else {
    //     console.log("Email not sent. \nResponse:", response);
    //   }
    // } catch (error) {
    //   console.error("Error sending email:", error);
    // }
  };

  // Validation function
  const validateInputs = () => {
    const hasRepeatedLetters = /(.)(\1)/g.test(name);
    const hasSymbols = /[!@#$%^&*(),.?":{}|<>]/g.test(name);
    const hasNumbers = /\d/g.test(name);

    if (name.length < 6) {
      setError("Name should be at least 6 letters");
      return false;
    } else if (hasRepeatedLetters) {
      setError("Name should not have repeated letters");
      return false;
    } else if (hasSymbols) {
      setError("Name should not contain symbols");
      return false;
    } else if (hasNumbers) {
      setError("Name should not contain numbers");
      return false;
    } else {
      setError("");
    }

    if (phone.length < 10) {
      setError1("Phone number should be at least 10 numbers");
      return false;
    } else {
      setError1("");
    }

    if (email.length > 50) {
      setError2("Email should not exceed 50 characters");
      return false;
    } else {
      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      if (!emailRegex.test(email)) {
        setError2("Invalid email format");
        return false;
      } else {
        setError2("");
      }
    }

    if (subject.length > 50) {
      setError3("Subject should not exceed 50 characters");
      return false;
    } else {
      setError3("");
    }

    if (message.length > 200) {
      setError4("Message should not exceed 200 characters");
      return false;
    } else {
      setError4("");
    }

    return true; // All validations passed
  };

  // name validation

  //Phone Number validation
  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    switch (name) {
      case "first-name":
        setName(value);
        break;
      case "phone-number":
        setPhone(value);
        break;
      case "email":
        setEmail(value);
        break;
      case "subject":
        setSubject(value);
        break;
      case "message":
        setMessage(value);
        break;
      default:
        break;
    }
  };

  return (
    <div className="portfolio">
      <div className="container mx-auto max-w-7xl px-2 sm:px-6 lg:px-8">
        <div className="-mx-4 flex  flex-wrap">
          <div className="w-full px-4">
            <div className=" mb-12 text-center mx-auto max-w-[510px] item-center lg:mb-20 ">
              <h2 className="text-5xl portfolio-text">Contact With Me</h2>
            </div>
          </div>
        </div>
      </div>
      <div className="flex flex-wrap  justify-center items-center">
        <div className=" portfolio-card w-[432px] h-[680px] rounded-3xl mr-[38px] p-6 mb-10">
          <div className="rectangle-3-2 w-[383px] h-[228px] rounded-3xl bg-gray-300 mt-4 ">
            <img
              src={Hands}
              alt=""
              className="rounded-3xl transition-all duration-500 hover:scale-105 hover:-translate-y-1"
            />
          </div>
          <div className="flex  text-2xl mt-10 font-bold text-[233,237,245] mb-4">
            Dasari Vinay Reddy
          </div>
          <p className="text-gray-500 mb-10">Software Engineer</p>
          <p className="text-gray-700 mb-8">
            I am available for freelance work. Connect with me via and call in
            to my account.
          </p>
          <p className="text-container mb-3">
            Phone : +91 7799262831
            <span className="underline"></span>
          </p>
          <p className="text-container">
            Email : rvinay968@gmail.com
            <span className="underline"></span>
          </p>
