import "./Main.css";
import Vinay from "../../../asstes/person.png";
import { motion } from "framer-motion";
import React, { useEffect, useState } from "react";
import { ReactTyped } from "react-typed";

const textVariants = {
  initial: {
    x: -500,
    opacity: 0,
  },
  animate: {
    x: 0,
    opacity: 1,
    transition: {
      duration: 1,
      staggerChildren: 0.1,
    },
  },
};
const imgVariants = {
  initial: {
    x: 500,
    opacity: 0,
  },
  animate: {
    x: 0,
    opacity: 1,
    transition: {
      duration: 1,
      staggerChildren: 0.1,
    },
  },
};
export const Section = () => {
  const [screenWidth, setScreenWidth] = useState(window.innerWidth);
  const [screenHeight, setScreenHeight] = useState(window.innerHeight);

  useEffect(() => {
    const handleResize = () => {
      setScreenWidth(window.innerWidth);
      setScreenHeight(window.innerHeight);
    };

    window.addEventListener("resize", handleResize);

    // Clean up the event listener when component unmounts
    return () => window.removeEventListener("resize", handleResize);
  }, []); // Empty dependency array ensures that effect runs only once

  return (
    <div className="container1">
      <div className="container mx-auto max-w-7xl px-2 sm:px-6 lg:px-8 ">
        <div className="hero">
          <motion.div
            className="hero_welcome"
            variants={textVariants}
            initial="initial"
            animate="animate"
          >
            {" "}
            <b className="hero_title">Welcome To My World</b> <br />
            Hi I'm <b className="vinay_title">Dasari Vinay Reddy</b>
            <br />
            <Typed
              strings={[
                "I'm a Front",
                "I Love Software Development",
                "I Love All My Subscribers",
              ]}
              typeSpeed={150}
              backSpeed={100}
              loop
            />
            <b className="vinay_sub">a Developer</b>
            <b className="vinay_sub">,Ui/Ux Designer</b>
            <p>Screen Width: {screenWidth}px</p>
            <p>Screen Height: {screenHeight}px</p>
          </motion.div>
        </div>
        <motion.div
          className="section"
          variants={imgVariants}
          initial="initial"
          animate="animate"
        >
          <div className="section__box">
            <div className="section__image">
              <img src={Vinay} alt="" />
            </div>
          </div>
        </motion.div>
      </div>
    </div>
  );
};
